.include "includes.s"
        
.bank $002 slot 3
.org $0

; wBuildOamController
Cx4_BuildOam:
; ram_ptr = the build oam controller singleton
	mov A, IMM $00                                                            ; $8000 / 0:00 / $6400
	add _100hA, IMM >(wBuildOamController-CX4_RAM_START)                      ; $8002 / 0:01 / $8606
	add _100hA, IMM <(wBuildOamController-CX4_RAM_START)                      ; $8004 / 0:02 / $8620
	mov OP RAM_PTR, A                                                         ; $8006 / 0:03 / $e01c

; R2 = shadow oam offset to start populating
	rdram lsb, IMM BuildOamController.oamOffsetToFill                         ; $8008 / 0:04 / $6c07
	rdram mid, IMM BuildOamController.oamOffsetToFill+1                       ; $800a / 0:05 / $6d08

	mov A, OP RAM_DTA                                                         ; $800c / 0:06 / $600c
	_and A, OP _00FFFF                                                        ; $800e / 0:07 / $b054
	mov OP R2, A                                                              ; $8010 / 0:08 / $e062

; A = num sprites to build. Skip building if 0
	rdram lsb, IMM BuildOamController.numSprites                              ; $8012 / 0:09 / $6c00
	mov A, OP RAM_DTA                                                         ; $8014 / 0:0a / $600c
	_and A, IMM $ff                                                           ; $8016 / 0:0b / $b4ff
	jz @hideLaterObjs                                                         ; $8018 / 0:0c / $0cda

; RD = num sprites to build
	mov OP RD, A                                                              ; $801a / 0:0d / $e06d

; R3 = 629 & $ff, eg 4
	rdram lsb, IMM $09                                                             ; $801c / 0:0e / $6c09
	mov A, OP RAM_DTA                                                             ; $801e / 0:0f / $600c
	_and A, IMM $ff                                                             ; $8020 / 0:10 / $b4ff
	mov OP R3, A                                                             ; $8022 / 0:11 / $e063

; R3 = $200 + R3, eg 204
	mov A, IMM $02                                                             ; $8024 / 0:12 / $6402
	add _100hA, OP R3                                                             ; $8026 / 0:13 / $8263
	mov OP R3, A                                                             ; $8028 / 0:14 / $e063

; R4 = 625 eg 0
	rdram lsb, IMM $05                                                             ; $802a / 0:15 / $6c05
	mov A, OP RAM_DTA                                                             ; $802c / 0:16 / $600c
	mov OP R4, A                                                             ; $802e / 0:17 / $e064

; R5 = 626 eg 10
	rdram lsb, IMM $06                                                             ; $8030 / 0:18 / $6c06
	mov A, OP RAM_DTA                                                             ; $8032 / 0:19 / $600c
	mov OP R5, A                                                             ; $8034 / 0:1a / $e065

; ram_ptr and RF = address of sprites to build
	mov A, IMM $00                                                            ; $8036 / 0:1b / $6400
	add _100hA, IMM >(wSpritesToBuild-CX4_RAM_START)                          ; $8038 / 0:1c / $8602
	add _100hA, IMM <(wSpritesToBuild-CX4_RAM_START)                          ; $803a / 0:1d / $8620
	mov OP RAM_PTR, A                                                         ; $803c / 0:1e / $e01c
	mov OP RF, A                                                              ; $803e / 0:1f / $e06f

@nextSprite:
; RE = 224 & $3f (eg 22)
	rdram lsb, IMM $04                                                             ; $8040 / 0:20 / $6c04
	mov A, OP RAM_DTA                                                             ; $8042 / 0:21 / $600c
	_and A, IMM $3f                                                             ; $8044 / 0:22 / $b43f
	mov OP RE, A                                                             ; $8046 / 0:23 / $e06e

; R7 = 225 & $ff (eg b0)
	rdram lsb, IMM SpriteToBuild.baseTileIdx                                                             ; $8048 / 0:24 / $6c05
	mov A, OP RAM_DTA                                                             ; $804a / 0:25 / $600c
	_and A, IMM $ff                                                             ; $804c / 0:26 / $b4ff
	mov OP R7, A                                                             ; $804e / 0:27 / $e067

; A = 621
	mov A, IMM $00                                                             ; $8050 / 0:28 / $6400
	add _100hA, IMM >$0621                                                             ; $8052 / 0:29 / $8606
	add _100hA, IMM <$0621                                                             ; $8054 / 0:2a / $8621

; R8 = word in 622/621 eg 0000
	rdram lsb, OP REG_A                                                             ; $8056 / 0:2b / $6800
	add A, IMM $01                                                             ; $8058 / 0:2c / $8401
	rdram mid, OP REG_A                                                             ; $805a / 0:2d / $6900
	mov A, OP RAM_DTA                                                             ; $805c / 0:2e / $600c
	extsw A                                                             ; $805e / 0:2f / $5a00
	mov OP R8, A                                                             ; $8060 / 0:30 / $e068

; A = word in 221/220 eg 00d0
	rdram lsb, IMM $00                                                             ; $8062 / 0:31 / $6c00
	rdram mid, IMM $01                                                             ; $8064 / 0:32 / $6d01
	mov A, OP RAM_DTA                                                             ; $8066 / 0:33 / $600c
	extsw A                                                             ; $8068 / 0:34 / $5a00

; R8 = A - R8
	sub A, OP R8                                                             ; $806a / 0:35 / $9068
	mov OP R8, A                                                             ; $806c / 0:36 / $e068

; A = 623
	mov A, IMM $00                                                             ; $806e / 0:37 / $6400
	add _100hA, IMM >$0623                                                             ; $8070 / 0:38 / $8606
	add _100hA, IMM <$0623                                                             ; $8072 / 0:39 / $8623

; R9 = word in 624/623 eg 0000
	rdram lsb, OP REG_A                                                             ; $8074 / 0:3a / $6800
	add A, IMM $01                                                             ; $8076 / 0:3b / $8401
	rdram mid, OP REG_A                                                             ; $8078 / 0:3c / $6900
	mov A, OP RAM_DTA                                                             ; $807a / 0:3d / $600c
	extsw A                                                             ; $807c / 0:3e / $5a00
	mov OP R9, A                                                             ; $807e / 0:3f / $e069

;  A = word in 223/222 eg 0031
	rdram lsb, IMM $02                                                             ; $8080 / 0:40 / $6c02
	rdram mid, IMM $03                                                             ; $8082 / 0:41 / $6d03
	mov A, OP RAM_DTA                                                             ; $8084 / 0:42 / $600c
	extsw A                                                             ; $8086 / 0:43 / $5a00

; R9 = A - R9
	sub A, OP R9                                                             ; $8088 / 0:44 / $9069
	mov OP R9, A                                                       ; $808a / 0:45 / $e069

; ext_ptr = addr of sprite spec (num objs, + 4 bytes per obj)
	rdram lsb, IMM SpriteToBuild.spriteSpecAddr                               ; $808c / 0:46 / $6c07
	rdram mid, IMM SpriteToBuild.spriteSpecAddr+1                             ; $808e / 0:47 / $6d08
	rdram msb, IMM SpriteToBuild.spriteSpecAddr+2                             ; $8090 / 0:48 / $6e09

	mov A, OP RAM_DTA                                                         ; $8092 / 0:49 / $600c
	mov OP EXT_PTR, A                                                         ; $8094 / 0:4a / $e013

; RC = 226 eg 0
	rdram lsb, IMM $06                                                             ; $8096 / 0:4b / $6c06
	mov A, OP RAM_DTA                                                             ; $8098 / 0:4c / $600c
	mov OP RC, A                                                       ; $809a / 0:4d / $e06c

; ram_ptr = R2 (shadow oam offset to populate)
	mov A, OP R2                                                              ; $809c / 0:4e / $6062
	mov OP RAM_PTR, A                                                         ; $809e / 0:4f / $e01c

; ext_dta = num objs for sprite
	mov ext_dta, OP SNES_ROM_EXT_PTR                                          ; $80a0 / 0:50 / $612e
	inc_ext_ptr                                                               ; $80a2 / 0:51 / $4000
	finish_ext_dta                                                            ; $80a4 / 0:52 / $1c00

; R1 = saved ext_ptr
	mov A, OP EXT_PTR                                                         ; $80a6 / 0:53 / $6013
	mov OP R1, A                                                              ; $80a8 / 0:54 / $e061

; RA = num objs
	mov A, OP EXT_DTA                                                         ; $80aa / 0:55 / $6003
	_and A, IMM $ff                                                           ; $80ac / 0:56 / $b4ff
	mov OP RA, A                                                              ; $80ae / 0:57 / $e06a

@nextObj:
; RB = 1st of 4 bytes for obj
	mov ext_dta, OP SNES_ROM_EXT_PTR                                                             ; $80b0 / 0:58 / $612e
	inc_ext_ptr                                                             ; $80b2 / 0:59 / $4000
	finish_ext_dta                                                             ; $80b4 / 0:5a / $1c00

	mov A, OP EXT_DTA                                                             ; $80b6 / 0:5b / $6003
	mov OP RB, A                                                             ; $80b8 / 0:5c / $e06b

; A = $f8 if bit 5 clear above else $f0
	_and A, IMM $20                                                             ; $80ba / 0:5d / $b420
	mov A, IMM $f8                                                             ; $80bc / 0:5e / $64f8
	skipz                                                             ; $80be / 0:5f / $2601
	mov A, IMM $f0                                                             ; $80c0 / 0:60 / $64f0

; R0 = above sign-extended
	extsb A                                                             ; $80c2 / 0:61 / $5900
	mov OP R0, A                                                             ; $80c4 / 0:62 / $e060

; Get ext_dta using ext_ptr (2nd of 4 bytes)
	mov ext_dta, OP SNES_ROM_EXT_PTR                                                             ; $80c6 / 0:63 / $612e
	inc_ext_ptr                                                             ; $80c8 / 0:64 / $4000
	finish_ext_dta                                                             ; $80ca / 0:65 / $1c00

; set carry if RC bit 6 is set
	mov A, OP RC                                                             ; $80cc / 0:66 / $606c
	_and A, IMM $40                                                             ; $80ce / 0:67 / $b440
	add A, OP _FFFFFF                                                             ; $80d0 / 0:68 / $8051

; sign-extend the 2nd obj byte
	mov A, OP EXT_DTA                                                             ; $80d2 / 0:69 / $6003
	extsb A                                                             ; $80d4 / 0:6a / $5900
	skipc                                                             ; $80d6 / 0:6b / $2501
	jp @cont_80e0                                                             ; $80d8 / 0:6c / $0870

; do A = R0 - A
	xor A, OP _FFFFFF                                                             ; $80da / 0:6d / $a851
	add A, IMM $01                                                             ; $80dc / 0:6e / $8401
	add A, OP R0                                                             ; $80de / 0:6f / $8060

@cont_80e0:
; A + R8 into R6 and ram_dta+0 (X), then clear its .lsb
	add A, OP R8                                                             ; $80e0 / 0:70 / $8068
	mov OP R6, A                                                             ; $80e2 / 0:71 / $e066
	mov OP RAM_DTA, A                                                             ; $80e4 / 0:72 / $e00c
	wrram lsb, IMM $00                                                             ; $80e6 / 0:73 / $ec00

; Get ext_dta using ext_ptr (3rd of 4 bytes)
	mov ext_dta, OP SNES_ROM_EXT_PTR                                                             ; $80e8 / 0:74 / $612e
	inc_ext_ptr                                                             ; $80ea / 0:75 / $4000
	finish_ext_dta                                                             ; $80ec / 0:76 / $1c00

; set carry if bit 7 set on RC
	mov A, OP RC                                                             ; $80ee / 0:77 / $606c
	_and A, IMM $80                                                             ; $80f0 / 0:78 / $b480
	add A, OP _FFFFFF                                                             ; $80f2 / 0:79 / $8051

; sign-extend the 3rd byte
	mov A, OP EXT_DTA                                                             ; $80f4 / 0:7a / $6003
	extsb A                                                             ; $80f6 / 0:7b / $5900
	skipc                                                             ; $80f8 / 0:7c / $2501
	jp @cont_8102                                                             ; $80fa / 0:7d / $0881

; do A = R0 - A
	xor A, OP _FFFFFF                                                             ; $80fc / 0:7e / $a851
	add A, IMM $01                                                             ; $80fe / 0:7f / $8401
	add A, OP R0                                                             ; $8100 / 0:80 / $8060

@cont_8102:
; A + R9 into ram_dta+1 (Y)
	add A, OP R9                                                             ; $8102 / 0:81 / $8069
	mov OP RAM_DTA, A                                                             ; $8104 / 0:82 / $e00c
	wrram lsb, IMM $01                                                             ; $8106 / 0:83 / $ec01
	add A, IMM $0f                                                             ; $8108 / 0:84 / $840f
	_cmp A, IMM $ef                                                             ; $810a / 0:85 / $54ef
	jc @br_817a                                                             ; $810c / 0:86 / $10bd

; Get ext_dta using ext_ptr (4th byte)
	mov ext_dta, OP SNES_ROM_EXT_PTR                                                             ; $810e / 0:87 / $612e
	inc_ext_ptr                                                             ; $8110 / 0:88 / $4000
	finish_ext_dta                                                             ; $8112 / 0:89 / $1c00

; ram_dta = ext_dta + R7
	mov A, OP EXT_DTA                                                             ; $8114 / 0:8a / $6003
	add A, OP R7                                                             ; $8116 / 0:8b / $8067
	mov OP RAM_DTA, A                                                             ; $8118 / 0:8c / $e00c

; set the tile idx
	wrram lsb, IMM $02                                                             ; $811a / 0:8d / $ec02

; get xflip/yflip in R0
	mov A, OP RC                                                             ; $811c / 0:8e / $606c
	_and A, IMM $c0                                                             ; $811e / 0:8f / $b4c0
	mov OP R0, A                                                             ; $8120 / 0:90 / $e060

; get flips + palette data | eg 22
	mov A, OP RB                                                             ; $8122 / 0:91 / $606b
	_and A, IMM $ce                                                             ; $8124 / 0:92 / $b4ce
	or A, OP RE                                                             ; $8126 / 0:93 / $b86e
	xor A, OP R0                                                             ; $8128 / 0:94 / $a860
	mov OP RAM_DTA, A                                                             ; $812a / 0:95 / $e00c

; set the tile attr
	wrram lsb, IMM $03                                                             ; $812c / 0:96 / $ec03

; R4 is its upper 6 bits (orig val in 625)
	mov A, OP R4                                                             ; $812e / 0:97 / $6064
	shlr A, IMM $02                                                             ; $8130 / 0:98 / $c402
	_and A, IMM $3f                                                             ; $8132 / 0:99 / $b43f
	mov OP R4, A                                                             ; $8134 / 0:9a / $e064

; get obj's high bit of X. if set, R4 += $40
	mov A, OP R6                                                             ; $8136 / 0:9b / $6066
	_and A, OP _000100                                                             ; $8138 / 0:9c / $b05e
	mov A, OP R4                                                             ; $813a / 0:9d / $6064
	skipz                                                             ; $813c / 0:9e / $2601
	or A, IMM $40                                                             ; $813e / 0:9f / $bc40
	mov OP R4, A                                                             ; $8140 / 0:a0 / $e064

	mov A, OP RB                                                             ; $8142 / 0:a1 / $606b
	shll A, IMM $12                                                             ; $8144 / 0:a2 / $dc12
	mov A, OP R4                                                             ; $8146 / 0:a3 / $6064
	skipns                                                             ; $8148 / 0:a4 / $2700
	or A, IMM $80                                                             ; $814a / 0:a5 / $bc80
	mov OP R4, A                                                             ; $814c / 0:a6 / $e064

; R5 += 1 (val in 626)
	mov A, OP R5                                                             ; $814e / 0:a7 / $6065
	add A, IMM $01                                                             ; $8150 / 0:a8 / $8401
	mov OP R5, A                                                             ; $8152 / 0:a9 / $e065
	_and A, IMM $03                                                             ; $8154 / 0:aa / $b403
	skipz                                                             ; $8156 / 0:ab / $2601
	jp @br_816e                                                             ; $8158 / 0:ac / $08b7

; swap ram_ptr and R3 eg ram_ptr = 204
	mov A, OP RAM_PTR                                                             ; $815a / 0:ad / $601c
	swap OP R3                                                             ; $815c / 0:ae / $f063
	mov OP RAM_PTR, A                                                             ; $815e / 0:af / $e01c

; store R4 val there
	mov A, OP R4                                                             ; $8160 / 0:b0 / $6064
	mov OP RAM_DTA, A                                                             ; $8162 / 0:b1 / $e00c
	wrram lsb, IMM $00                                                             ; $8164 / 0:b2 / $ec00

; inc ram_ptr eg to 205 then get orig ram_ptr in A
	mov A, OP RAM_PTR                                                             ; $8166 / 0:b3 / $601c
	add A, IMM $01                                                             ; $8168 / 0:b4 / $8401
	swap OP R3                                                             ; $816a / 0:b5 / $f063
	jp +                                                             ; $816c / 0:b6 / $08b8

@br_816e:
	mov A, OP RAM_PTR                                                             ; $816e / 0:b7 / $601c

; ram_ptr for 220+ region += 4, and into R2
+	add A, IMM $04                                                             ; $8170 / 0:b8 / $8404
	mov OP RAM_PTR, A                                                             ; $8172 / 0:b9 / $e01c
	mov OP R2, A                                                             ; $8174 / 0:ba / $e062

;
	shll A, IMM $0e                                                             ; $8176 / 0:bb / $dc0e
	js @done                                                             ; $8178 / 0:bc / $14ec

@br_817a:
; R1 += 4 (ext_ptr)
	mov A, OP R1                                                             ; $817a / 0:bd / $6061
	add A, IMM $04                                                             ; $817c / 0:be / $8404
	mov OP R1, A                                                             ; $817e / 0:bf / $e061
	mov OP EXT_PTR, A                                                             ; $8180 / 0:c0 / $e013

; RA -= 1, looping if non-0
	mov A, OP RA                                                             ; $8182 / 0:c1 / $606a
	sub A, IMM $01                                                             ; $8184 / 0:c2 / $9401
	mov OP RA, A                                                             ; $8186 / 0:c3 / $e06a
	skipz                                                             ; $8188 / 0:c4 / $2601
	jp @nextObj                                                             ; $818a / 0:c5 / $0858

	mov A, OP RF                                                             ; $818c / 0:c6 / $606f
	add A, IMM $10                                                             ; $818e / 0:c7 / $8410
	mov OP RF, A                                                             ; $8190 / 0:c8 / $e06f
	mov OP RAM_PTR, A                                                             ; $8192 / 0:c9 / $e01c

; RD -= 1, once 0, continue ahead to start end sequence
	mov A, OP RD                                                             ; $8194 / 0:ca / $606d
	sub A, IMM $01                                                             ; $8196 / 0:cb / $9401
	mov OP RD, A                                                             ; $8198 / 0:cc / $e06d
	skipz                                                             ; $819a / 0:cd / $2601
	jp @nextSprite                                                             ; $819c / 0:ce / $0820

	mov A, OP R5                                                             ; $819e / 0:cf / $6065
	sub IMM $04, A                                                             ; $81a0 / 0:d0 / $8c04
	_and _2A, IMM $06                                                             ; $81a2 / 0:d1 / $b506
	jz @hideLaterObjs                                                             ; $81a4 / 0:d2 / $0cda

	mov OP R0, A                                                             ; $81a6 / 0:d3 / $e060
	mov A, OP R4                                                             ; $81a8 / 0:d4 / $6064
	shlr A, OP R0                                                             ; $81aa / 0:d5 / $c060
	mov OP RAM_DTA, A                                                             ; $81ac / 0:d6 / $e00c
	mov A, OP R3                                                             ; $81ae / 0:d7 / $6063
	mov OP RAM_PTR, A                                                             ; $81b0 / 0:d8 / $e01c
	wrram lsb, IMM $00                                                             ; $81b2 / 0:d9 / $ec00

@hideLaterObjs:
; ram_ptr = eg $6040
	mov A, OP R2                                                             ; $81b4 / 0:da / $6062
	mov OP RAM_PTR, A                                                             ; $81b6 / 0:db / $e01c

; shr gives eg $10, then do $7f-$10 (num remaining sprites), returning if borrow (breached $7f)
	shlr A, IMM $02                                                             ; $81b8 / 0:dc / $c402
	sub IMM $7f, A                                                             ; $81ba / 0:dd / $8c7f
	skipc                                                             ; $81bc / 0:de / $2501
	jp @done                                                             ; $81be / 0:df / $08ec

; R0 = A, A and ram_dta = $f0 (Y value)
	mov OP R0, A                                                             ; $81c0 / 0:e0 / $e060
	mov A, IMM $f0                                                             ; $81c2 / 0:e1 / $64f0
	mov OP RAM_DTA, A                                                             ; $81c4 / 0:e2 / $e00c

@hideNextObj:
; put ram_dta.lsb into $621
	wrram lsb, IMM $01                                                             ; $81c6 / 0:e3 / $ec01

; ram_ptr += 4
	mov A, OP RAM_PTR                                                             ; $81c8 / 0:e4 / $601c
	add A, IMM $04                                                             ; $81ca / 0:e5 / $8404
	mov OP RAM_PTR, A                                                             ; $81cc / 0:e6 / $e01c

; R0 -= 1
	mov A, OP R0                                                             ; $81ce / 0:e7 / $6060
	sub A, IMM $01                                                             ; $81d0 / 0:e8 / $9401
	mov OP R0, A                                                             ; $81d2 / 0:e9 / $e060

; done once negative
	skips                                                             ; $81d4 / 0:ea / $2701
	jp @hideNextObj                                                             ; $81d6 / 0:eb / $08e3

@done:
	halt                                                             ; $81d8 / 0:ec / $fc00


.org $200

Cx4_ScaleTiles:
	mov A, OP R3                                                             ; $8200 / 1:00 / $6063
	mov OP EXT_DTA, A                                                             ; $8202 / 1:01 / $e003
	_cmp A, OP _000000                                                             ; $8204 / 1:02 / $5050
	jz +                                                             ; $8206 / 1:03 / $0c05
	call Func_2_834a                                                             ; $8208 / 1:04 / $28a5
